  //     activebtn[i].className = activebtn[i].className.replace(" w3-dark-grey", "");
  //   }
  //   document.getElementById(cityName).style.display = "block";
  //   evt.currentTarget.className += " w3-dark-grey";
  // }

  // var mybtn = document.getElementsByClassName("testbtn")[0];
  // mybtn.click();

  // // Accordions
  // function myAccFunc(id) {
  //   var x = document.getElementById(id);
  //   if (x.className.indexOf("w3-show") == -1) {
  //     x.className += " w3-show";
  //   } else {
  //     x.className = x.className.replace(" w3-show", "");
  //   }
  // }

  // // Slideshows
  // var slideIndex = 1;

  // function plusDivs(n) {
  //   slideIndex = slideIndex + n;
  //   showDivs(slideIndex);
  // }

  // function showDivs(n) {
  //   var x = document.getElementsByClassName("mySlides");
  //   if (n > x.length) { slideIndex = 1 }
  //   if (n < 1) { slideIndex = x.length };
  //   for (var i = 0; i < x.length; i++) {
  //     x[i].style.display = "none";
  //   }
  //   x[slideIndex - 1].style.display = "block";
  // }

  // showDivs(1);

  // // Progress Bars
  // function move() {
  //   var elem = document.getElementById("myBar");
  //   var width = 5;
  //   var id = setInterval(frame, 10);
  //   function frame() {
  //     if (width == 100) {
  //       clearInterval(id);
  //     } else {
  //       width++;
  //       elem.style.width = width + '%';
  //       elem.innerHTML = width * 1 + '%';
  //     }
  //   }
  // }

  return (
    <Fragment>
      <header class="w3-container w3-theme w3-padding" id="myHeader">
        <div class="w3-center">
          <h4>BEAUTIFUL RESPONSIVE WEB SITES</h4>
          <h1 class="w3-xxxlarge w3-animate-bottom">BUILT WITH CODING</h1>
          <div class="w3-padding-32">
            <button class="w3-btn w3-xlarge w3-dark-grey w3-hover-light-grey" onclick="document.getElementById('id01').style.display='block'" >Services</button>
          </div>
        </div>
      </header>

      <div id="id01" class="w3-modal">
        <div class="w3-modal-content w3-card-4 w3-animate-top">
          <header class="w3-container w3-theme-l1">
            <span onclick="document.getElementById('id01').style.display='none'"
              class="w3-button w3-display-topright">×</span>
            <h4>Oh snap! We just showed you a modal..</h4>
            <h5>Because we can <i class="fa fa-smile-o"></i></h5>
          </header>
          <div class="w3-padding">
            <p>Cool huh? Ok, enough teasing around..</p>
            <p>Go to our <a class="w3-btn" href="/w3css/default.asp">Web develop</a> to learn more!</p>
          </div>
          <footer class="w3-container w3-theme-l1">
            <p>Modal footer</p>
          </footer>
        </div>
      </div>

      <div id="services" className="container-fluid text-center">
        <h2>SERVICES</h2>
        <h4>What we offer</h4>
        <br />
        <div className="row slideanim">
          <div className="col-sm-4">
            <span className="glyphicon glyphicon-off logo-small"></span>
            <h4>POWER</h4>
            <p>Lorem ipsum dolor sit amet..</p>
          </div>
          <div className="col-sm-4">
            <span className="glyphicon glyphicon-heart logo-small"></span>
            <h4>LOVE</h4>
            <p>Lorem ipsum dolor sit amet..</p>
          </div>
          <div className="col-sm-4">
            <span className="glyphicon glyphicon-lock logo-small"></span>
            <h4>JOB DONE</h4>
            <p>Lorem ipsum dolor sit amet..</p>
          </div>
        </div>
        <br /><br />
        <div className="row slideanim">
          <div className="col-sm-4">
            <span className="glyphicon glyphicon-leaf logo-small"></span>
            <h4>GREEN</h4>
            <p>Lorem ipsum dolor sit amet..</p>
          </div>
          <div className="col-sm-4">
            <span className="glyphicon glyphicon-certificate logo-small"></span>
            <h4>CERTIFIED</h4>
            <p>Lorem ipsum dolor sit amet..</p>
          </div>
          <div className="col-sm-4">
            <span className="glyphicon glyphicon-wrench logo-small"></span>
            <h4 style={{ color: "#303030" }}>HARD WORK</h4>
            <p>Lorem ipsum dolor sit amet..</p>
          </div>
        </div>
      </div>

    </Fragment>
  );
}

export default Services;
